mapper.select:
	select * from 
	(select /*+index_desc(a XPKATTEND) */	
	rownum rn, a.emp_no, a.attend_date, e.emp_name, a.attend_startTime, a.attend_endTime, a.attend_stateNo
	from attend a, emp e
	
		
			a.emp_no=e.emp_no
			
				and emp_name like #{searchName}
			
			
				and to_date(attend_date, 'yy-mm-dd HH24:MI:SS') BETWEEN
				to_char(#{from}) AND to_char(#{to})
			
		

	
	and rownum<=#{pageNum}*#{amount}
	)
		where rn>(#{pageNum}-1)*#{amount}

select count(*) from (select /*+index_desc(attend XPKATTEND) */ rownum rn, a.emp_no, a.attend_date, e.emp_name, a.attend_startTime, a.attend_endTime, a.attend_stateNo from attend a, emp e
a.emp_no=e.emp_no
and emp_name like #{searchName}
and to_date(attend_date, 'yy-mm-dd HH24:MI:SS') BETWEEN to_char(#{from}) AND to_char(#{to})
)

select TO_CHAR(attend_date, 'YYYY-MM-DD') from attend
where emp_no=#{emp_no}
and TO_CHAR(attend_date,'YYYY-MM-DD')=TO_CHAR(sysdate, 'YYYY-MM-DD')

select attend_endTime from attend
WHERE emp_no=#{emp_no}
and TO_CHAR(attend_date,'YYYY-MM-DD')=TO_CHAR(sysdate, 'YYYY-MM-DD')
